/** @jsx jsx */
import { jsx, css } from "@emotion/core"
import tw from "twin.macro"

import React, { useState } from "react"

import { useSelector, useDispatch } from "react-redux"
import { create, collectionSlice } from "./collectionSlice"

const Collection = () => {
  const [collectionName, setCollectionName] = useState("")
  const dispatch = useDispatch()

  const handleCollectionCreation = e => {
    e.preventDefault()
    dispatch()
  }

  return (
    <section tw="flex flex-1 flex-col h-screen bg-red-500">
      <div tw="py-2 px-2">
        <h3 tw="text-lg"> Your Collection </h3>
        <form onSubmit={handleCollectionCreation}>
          Add collection
          <input
            value={collectionName}
            onChange={e => setCollectionName(e.target.value)}
          />
          <button tw="bg-purple-500 rounded p-2" type="submit">
            add collection
          </button>
        </form>
      </div>
      <div tw="bg-pink-500">
        <p> Collection 1 </p>
        <p> Collection 2 </p>
      </div>
    </section>
  )
}

export default Collection
