/** @jsx jsx */
import { jsx, css } from "@emotion/core"
import tw from "twin.macro"

import axios from "axios"
import React, { useState, useEffect } from "react"

const URL = "https://pokeapi.co/api/v2/pokemon?limit=150"

const PokeCard = props => {
  return (
    <div tw="flex flex-row justify-around items-center bg-red-500 p-2">
      <div tw="">
        <img
          tw="bg-cover"
          alt="pokemon"
          src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png"
        />
      </div>
      <div tw="mx-1">
        <p>name: {props.pokemonName}</p>
        <p>type</p>
        <p>stats</p>
      </div>
      <div tw="mx-1">
        <button tw="bg-gray-300 p-1 rounded">Add to collection</button>
      </div>
    </div>
  )
}

const PokemonCards = () => {
  const [pokeData, setPokeData] = useState([])

  useEffect(() => {
    const fetchPokemon = async () => {
      const result = await axios.get(URL)
      console.log(result.data)
      console.log(result.data.results[0].name)
      setPokeData(result.data.results[0].name)
    }
    fetchPokemon()
  }, [])

  return (
    <div tw="p-2">
      Pokemon Cards
      <section tw="grid grid-cols-1 gap-2">
        <PokeCard pokemonName={pokeData.results[0].name} />
      </section>
    </div>
  )
}

export default PokemonCards
