import { jsx, css } from "@emotion/core"
import tw from "twin.macro"

import axios from "axios"
import React, { useState, useEffect } from "react"
import { useSelector, useDispatch } from "react-redux"

// const URL = "https://pokeapi.co/api/v2/pokemon?limit=10"

const PokeCard = props => {
  const { pokemonName } = props

  return (
    <div tw="flex flex-row justify-around items-center bg-red-500 p-2">
      <div tw="">
        <img
          tw="bg-cover"
          alt="pokemon"
          src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png"
        />
      </div>
      <div tw="mx-1">
        <p>{pokemonName}</p>
        <p>type</p>
        <p>stats</p>
      </div>
      <div tw="mx-1">
        <button tw="bg-gray-300 p-1 rounded">Add to collection</button>
      </div>
    </div>
  )
}

const PokemonCards = () => {
  const [pokeData, setPokeData] = useState([])
  const dispatch = useDispatch()

  // useEffect(() => {
  //   try {
  //     const fetchPokemon = async () => {
  //       const result = await axios.get(URL)
  //       setPokeData(result.data.results)
  //     }
  //     fetchPokemon()
  //   } catch (e) {
  //     console.log("Error fetching pokemon list.")
  //   }
  // }, [])

  return (
    <div tw="p-2">
      Pokemon Cards
      <section tw="grid grid-cols-1 gap-2">
        {pokeData.map(poke => (
          <ul key={poke.url}>
            <li>
              <PokeCard pokemonName={poke.name} />
            </li>
          </ul>
        ))}
      </section>
    </div>
  )
}

export default PokemonCards
