import { createSlice } from "@reduxjs/toolkit"
import axios from "axios"

const URL = "https://pokeapi.co/api/v2/pokemon?limit=10"

const initialState = {
  pokemonList: [],
  pokemonDetails: [],
}

export const pokemonCardsSlice = createSlice({
  name: "pokemonCards",
  initialState: initialState,
  reducers: {
    getData: (state, action) => {
      state.pokemonList = action.payload
    },
  },
})

export const { getData } = pokemonCardsSlice.actions

export const fetchPokemonNameUrl = () => {
  return async dispatch => {
    try {
      const pokemonName = await axios.get(URL).data.results
      const pokemonDet = pokemonName.map(async poke => {
        const d = await axios.get(poke.url)
        console.log(d)
      })
      dispatch(getData(pokemonName))
    } catch (e) {
      console.log("Could not fetch data.")
    }
  }
}

export const selectorPokemon = state => state.pokemon.pokemonList

export default pokemonCardsSlice.reducer
