/** @jsx jsx */
import { jsx, css } from "@emotion/core"
import tw from "twin.macro"
import pikachu from "./assets/images/pokeball_phone.jpg"

import React from "react"

const Landing = () => {
  return (
    <div
      css={[
        tw`flex flex-1 h-screen w-full bg-auto bg-center bg-no-repeat`,
        css`
          background: url(${pikachu});
        `,
      ]}
    >
      POKEMON
    </div>
  )
}

export default Landing
