/** @jsx jsx */
import { jsx, css } from "@emotion/core"
import tw from "twin.macro"

import React, { useState } from "react"

import { useSelector, useDispatch } from "react-redux"
import { create, selectorCollection } from "./collectionSlice"

const Collection = () => {
  const [collectionName, setCollectionName] = useState("")
  const dispatch = useDispatch()
  const collection = useSelector(selectorCollection)

  const handleCollectionCreation = e => {
    e.preventDefault()
    dispatch(create(collectionName))
    setCollectionName("")
  }

  return (
    <div tw="p-2 md:p-4">
      <section>
        <h3 tw="text-lg md:text-xl"> Your Collection </h3>
        <form onSubmit={handleCollectionCreation}>
          <input
            tw="rounded bg-gray-500"
            value={collectionName}
            onChange={e => setCollectionName(e.target.value)}
          />
          <button
            tw="bg-purple-500 text-gray-200 hover:text-white rounded p-1 m-1"
            type="submit"
          >
            create collection
          </button>
        </form>
      </section>
      <section>
        {collection.map(col => (
          <p tw="bg-blue-800 text-white p-8 rounded my-2" key={col.id}>
            {col.name}
            {col.pokemons.map(poke => (
              <p> {poke} </p>
            ))}
          </p>
        ))}
      </section>
    </div>
  )
}

export default Collection
