import { createSlice } from "@reduxjs/toolkit"
import axios from "axios"

const initialState = {
  isLoading: false,
  hasErrors: false,
  pokemon: [],
}

const URL = "https://pokeapi.co/api/v2/pokemon?limit=10"

export const pokemonCardsSlice = createSlice({
  name: "pokemonCards",
  initialState: initialState,
  reducers: {
    getPokemon: state => {
      state.isLoading = true
    },
    getPokemonSuccess: (state, action) => {
      state.isLoading = false
      state.hasErrors = false
      state.pokemon = action.payload
    },
    getPokemonFailure: state => {
      state.isLoading = false
      state.hasErrors = true
    },
  },
})

export const {
  getPokemon,
  getPokemonSuccess,
  getPokemonFailure,
} = pokemonCardsSlice.actions

// useEffect(() => {
//   try {
//     const fetchPokemon = async () => {
//       const result = await axios.get(URL)
//       setPokeData(result.data.results)
//     }
//     fetchPokemon()
//   } catch (e) {
//     console.log("Error fetching pokemon list.")
//   }
// }, [])

export const fetchPokemon = () => {
  return async dispatch => {
    getPokemon()
    try {
      const response = await axios.get(URL).data.results
      dispatch(getPokemonSuccess(response))
    } catch (e) {
      dispatch(getPokemonFailure)
    }
  }
}

export const pokemonSelector = state => state.pokemon

export default pokemonCardsSlice.reducer
