import { jsx, css } from "@emotion/core"
import tw from "twin.macro"

import React from "react"

const UserInfo = () => {
  const dateTime = new Date()
  const currentDate = dateTime.toDateString()

  return (
    <div tw="flex flex-row justify-around items-center bg-gray-900 text-white py-8 px-2">
      <section tw="w-1/3 text-center bg-gray-200 rounded p-4 mr-1">
        <h3 tw="text-xl"> Hello User!</h3>
        <p> {currentDate} </p>
      </section>
      <section tw="w-2/3 text-center bg-yellow-300 p-4 rounded ml-1">
        <p> Your balance is = XXX</p>
        <p> number of collections = YYY</p>
      </section>
    </div>
  )
}

export default UserInfo
