/** @jsx jsx */
import { jsx, css } from "@emotion/core"
import tw from "twin.macro"
import React from "react"

const CollectionCard = props => {
  const { collection } = props

  return (
    <section tw="flex flex-row bg-blue-800 h-full shadow-lg text-white rounded p-8 my-2">
      <div tw="w-1/3 mx-1 my-auto text-xl md:text-lg">
        <h3 tw="bg-gray-800 rounded px-1 py-4 text-center font-bold text-lg md:text-xl lg:text-2xl">
          {collection.name}
        </h3>
      </div>
      <div tw="w-2/3 mx-1 my-auto bg-red-100 text-center sm:text-lg md:text-base">
        {collection.pokemons.map(poke => (
          <p> {poke} </p>
        ))}
      </div>
    </section>
  )
}

export default CollectionCard
